func:
// Processar estado e atualizar dashboard
const data = msg.payload.data || {};
const metrics = msg.payload.metrics || {};

// Atualizar posição do drone para gráfico
const posX = data.drone_position ? data.drone_position[0] : 0;
const posY = data.drone_position ? data.drone_position[1] : 0;
const posZ = data.drone_position ? data.drone_position[2] : 0;

// Mensagem para gráfico de trajetória (X, Y) - formato correto para ui_chart
const trajectoryMsg = {
    payload: [
        {series: 'X', x: Date.now(), y: posX},
        {series: 'Y', x: Date.now(), y: posY}
    ]
};

// Mensagem para altitude
const altitudeMsg = {
    payload: posZ
};

// Mensagem para métricas
const metricsMsg = {
    payload: {
        time: metrics.elapsed_time ? metrics.elapsed_time.toFixed(1) + 's' : '0s',
        distance: metrics.total_distance ? metrics.total_distance.toFixed(2) + 'm' : '0m',
        replans: metrics.replan_count || 0,
        efficiency: metrics.efficiency ? (metrics.efficiency * 100).toFixed(1) + '%' : '0%'
    }
};

// Mensagem para velocidade
const speed = data.drone_velocity ? Math.sqrt(
    Math.pow(data.drone_velocity[0], 2) +
    Math.pow(data.drone_velocity[1], 2) +
    Math.pow(data.drone_velocity[2], 2)
) : 0;

const velocityMsg = {
    payload: speed
};

// Mensagem para contadores
const countersMsg = {
    payload: {
        detected: data.detected_points_count || 0,
        delivered: data.delivered_points_count || 0,
        route_length: data.route_length || 0
    }
};

// Mensagem para cobertura (estimada baseada em pontos detectados)
const totalPoints = 10; // Total de pontos na simulação
const coverage = data.detected_points_count ? (data.detected_points_count / totalPoints * 100) : 0;
const coverageMsg = {
    payload: coverage
};

// Mensagem para energia (estimada baseada em distância)
const energy = metrics.total_distance ? (metrics.total_distance * 0.5) : 0; // ~0.5J por metro
const energyMsg = {
    payload: energy
};

return [trajectoryMsg, altitudeMsg, metricsMsg, velocityMsg, countersMsg, coverageMsg, energyMsg];

// Processar detecção
const data = msg.payload.data || {};
const detections = context.get('detections') || 0;
context.set('detections', detections + 1);

// Adicionar à lista de pontos detectados
const points = context.get('detected_points') || [];
const existingPoint = points.find(p => p.id === data.point_id);
if (!existingPoint) {
    points.push({
        id: data.point_id,
        position: data.point_position,
        timestamp: data.timestamp,
        delivered: false
    });
    context.set('detected_points', points);
}

msg.payload = {
    topic: 'detection',
    payload: detections + 1
};

// Atualizar tabela também
const tableMsg = {
    payload: {
        topic: 'update_table',
        payload: true
    }
};

return [msg, tableMsg];

// Processar entrega
const data = msg.payload.data || {};
const deliveries = context.get('deliveries') || 0;
context.set('deliveries', deliveries + 1);

// Atualizar lista de pontos entregues
const points = context.get('detected_points') || [];
const point = points.find(p => p.id === data.point_id);
if (point) {
    point.delivered = true;
    point.delivery_time = data.timestamp;
    context.set('detected_points', points);
}

msg.payload = {
    topic: 'delivery',
    payload: deliveries + 1
};

// Atualizar tabela também
const tableMsg = {
    payload: {
        topic: 'update_table',
        payload: true
    }
};

return [msg, tableMsg];

// Processar replanejamento
const data = msg.payload.data || {};
const replans = context.get('replans') || 0;
context.set('replans', replans + 1);

msg.payload = {
    topic: 'replan',
    payload: replans + 1
};

return msg;

// Atualizar tabela de pontos
const points = context.get('detected_points') || [];

// Formatar para exibição em texto HTML
if (points.length === 0) {
    msg.payload = {
        topic: 'points_table',
        payload: '<p style="padding: 10px;">Nenhum ponto detectado ainda.</p>'
    };
} else {
    let html = '<div style="overflow-x: auto;"><table style="width:100%; border-collapse: collapse; font-size: 12px;">';
    html += '<thead><tr style="background-color: #4CAF50; color: white;">';
    html += '<th style="padding: 8px; border: 1px solid #ddd;">ID</th>';
    html += '<th style="padding: 8px; border: 1px solid #ddd;">X</th>';
    html += '<th style="padding: 8px; border: 1px solid #ddd;">Y</th>';
    html += '<th style="padding: 8px; border: 1px solid #ddd;">Z</th>';
    html += '<th style="padding: 8px; border: 1px solid #ddd;">Status</th>';
    html += '<th style="padding: 8px; border: 1px solid #ddd;">Detectado</th>';
    html += '</tr></thead><tbody>';

    points.forEach(p => {
        const statusColor = p.delivered ? '#4CAF50' : '#FF9800';
        const statusText = p.delivered ? '✓ Entregue' : '⏳ Pendente';
        const detectedTime = new Date(p.timestamp * 1000).toLocaleTimeString();

        html += '<tr style="background-color: ' + (p.delivered ? '#e8f5e9' : '#fff3e0') + ';">';
        html += '<td style="padding: 8px; border: 1px solid #ddd; text-align: center;">' + p.id + '</td>';
        html += '<td style="padding: 8px; border: 1px solid #ddd; text-align: center;">' + p.position[0].toFixed(2) + '</td>';
        html += '<td style="padding: 8px; border: 1px solid #ddd; text-align: center;">' + p.position[1].toFixed(2) + '</td>';
        html += '<td style="padding: 8px; border: 1px solid #ddd; text-align: center;">' + p.position[2].toFixed(2) + '</td>';
        html += '<td style="padding: 8px; border: 1px solid #ddd; text-align: center; color: ' + statusColor + '; font-weight: bold;">' + statusText + '</td>';
        html += '<td style="padding: 8px; border: 1px solid #ddd; text-align: center;">' + detectedTime + '</td>';
        html += '</tr>';
    });

    html += '</tbody></table></div>';

    msg.payload = {
        topic: 'points_table',
        payload: html
    };
}

return msg;
